<?if(!defined('WWW_ROOT'))	die("access denided (config file)");// установить уровень сообщений об ошибках (максимальный)error_reporting(E_ALL | E_STRICT);ini_set('display_errors', 1);########## КОНФИГУРАЦИЯ ОБРАБОТЧИКА ОШИБОК ##########Core_Error_Model::setConfig(array(	'display'            => 1,               // отображать ошибки или нет	'minPermsForDisplay' => 0,               // минимальные права для отображения ошибок	'keepFileLog'        => 0,               // вести лог ошибок в файл	'fileLogPath'        => FS_ROOT.'logs/', // пусть хранения лог-файла	'keepDbLog'          => 0,               // вести лог ошибок в базу данных	'dbTableName'        => 'error_log',     // имя таблицы в БД	'keepDbSessionDump'  => 0,               // сохранять дамп сессии пользователя (только при включенном DB-логе)	'keepEmailLog'       => 0,               // отправлять сообщения об ошибках на email));?>