<?class Debugger {		private static $_config = array(	);			/**	 * Задать конфигурацию класса	 * @param array $config - массив директива=>значение	 * @return void;	 */	public static function setConfig($config){			foreach($config as $key => $val){			if(array_key_exists($key, self::$_config)){				self::$_config[$key] = $val;			}else{				trigger_error('Не удалось установить конфигурацию обработчика обшибок. Неизвестный ключ ['.$key.']', E_USER_ERROR);			}		}	}		/**	 * Получить значение конфигурационной директивы, или весь массив конфигурации	 * @param null|string $key	 * @return array|string	 */	public static function getConfig($key = null){				return is_null($key)			? self::$_config			: self::$_config[$key];	}		private function __construct(){		}		public function log($string){		}	}?>